import React from 'react'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import axios from 'axios'
import doc3 from '../../resources/doc3.jpg'

const SpecificDoctor = () => {
    let { docId } = useParams();
    const [specificDoctor, setSpecificDoctor] = useState({});
    useEffect(()=>{
        axios.get(`http://localhost:5001/doctors/byId/${docId}`).then((response)=>{
            setSpecificDoctor(response.data)
        })
      },[])

    
  return (
    <div>
        <Grid container spacing={7} rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
                        <Grid item xs={12} md={6} lg={4}>
                            <Card sx={{ maxWidth: 350 }}>
                                <CardMedia component="img" height="140" width="100" image={doc3}/>
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="div">{specificDoctor.doctorName}</Typography>
                                    <Typography variant="h6" color="text.secondary">{specificDoctor.doctorSpecialization}</Typography>
                                    <Typography variant="body" color="text.secondary">Email: {specificDoctor.doctorEmail}</Typography>
                                </CardContent>
                                {/* <CardActions sx={{paddingLeft: "21%" }}>
                                    <Button size="small" variant="contained"  onClick={()=>{navigate(`/onedoc/${value.doctorId}`)}}>Book an Appointment</Button>
                                </CardActions> */}
                            </Card>
                        </Grid>
                </Grid>
    </div>
  )
}

export default SpecificDoctor
