import React from 'react'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import axios from 'axios'
import doc3 from '../../resources/doc3.jpg'
import '../../App.css'

import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';

const SpecificDoctor = () => {
    let { docId } = useParams();
    const [specificDoctor, setSpecificDoctor] = useState({});
    useEffect(()=>{
        axios.get(`http://localhost:5001/doctors/byId/${docId}`).then((response)=>{
            setSpecificDoctor(response.data)
        })
      },[])

    
  return (
    <div>
        <div className="specificPage">
      <div className="leftSide">
        <div className="specific" id="individual">
          <div className="title"> {specificDoctor.doctorName} </div>
          <div className="body">{specificDoctor.doctorSpecialization}</div>
          <div className="footer">{specificDoctor.doctorExperience}</div>
        </div>
      </div>
      <div className="rightSide">Comment Section</div>
      <div>
        
      </div>
    </div>
    </div>
  )
}

export default SpecificDoctor
