import React from 'react'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import axios from 'axios'
import doc3 from '../../resources/doc3.jpg'

import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';

const SpecificDoctor = () => {
    let { docId } = useParams();
    const [specificDoctor, setSpecificDoctor] = useState({});
    useEffect(()=>{
        axios.get(`http://localhost:5001/doctors/byId/${docId}`).then((response)=>{
            setSpecificDoctor(response.data)
        })
      },[])

    
  return (
    <div>
        <Grid container spacing={2} rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }} className="specificDocContainer">
                        <Grid item xs={12}>
                            <Card sx={{ maxWidth: 350 }}>
                                <CardMedia component="img" height="150" width="100" image={doc3}/>
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="div">{specificDoctor.doctorName}</Typography>
                                    <Typography variant="h6" color="text.secondary">{specificDoctor.doctorSpecialization}</Typography>
                                    <Typography variant="body" color="text.secondary">{specificDoctor.doctorExperience} of Service</Typography>
                                </CardContent>

                            </Card>
                        </Grid>
                </Grid>
    </div>
  )
}

export default SpecificDoctor
